mapper.select:
select u.id, u.login, u.email, u.dob, r.id as role_id, r.name
from users u
         left join users_roles ur on u.id = ur.user_id
         left join roles r on r.id = ur.roles_id
where u.login = #{login}
  and u.pass = #{password}

mapper.delete:
delete
from users u
where u.id = #{id}

delete
from users_roles ur
where ur.user_id = #{id}

mapper.update:
update users
set pass = #{newPassword}
where login = #{login}

update users
set login = #{newLogin},
    email = #{email},
    dob   = #{dob},
    pass  = #{newPassword}
where login = #{login}
